import React, { Component } from 'react'
import api from '../../service/api'
import './styles.css'

export default class Form extends Component {
  constructor(props) {
    super(props);
    this.state = { value: '', string: 'BubbleSort' }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange(e) {
    this.setState({value: e.target.value});
  }

  async handleSubmit(e) {
    e.preventDefault();
    const arrayGenerate = this.state.value.split(',')
    console.log(arrayGenerate)
    // const response = api.post(`${this.state.string}`, {});
  }

  handleClick(e) {
    this.setState({string: e.target.value})
  }

  render() {
    return (
      <div className="form-container">
        <form className="selections" onSubmit={this.handleSubmit}>
          <div className="input-array">
            <label>Conjunto de n√∫meros:</label><input type="text" placeholder="Ex...: 12, 5, 22, 13, 5"
             value={this.state.value} onChange={this.handleChange}/>
          </div>
          <input type="radio" name="Ordenation" value="BubbleSort" onClick={this.handleClick} defaultChecked/>BubbleSort
          <br />
          <input type="radio" name="Ordenation" value="InsertionSort" onClick={this.handleClick}/>InsertionSort
          <br />
          <input type="radio" name="Ordenation" value="SelectionSort" onClick={this.handleClick}/>SelectionSort
          <input type="submit" value="Enviar"/>
        </form>
      </div>
    )
  }
}
