import React, { Component } from 'react'
// import api from '../../service/api'
import './styles.css'

export default class Form extends Component {
  constructor(props) {
    super(props);
    this.state = { value: '' }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange(e) {
    this.setState({value: e.target.value});
  }

  handleSubmit(e) {
    e.preventDefault();
  }

  handleClick(e) {
    this.setState({name: e.target.value})
  }

  render() {
    console.log(this.state.value);
    return (
      <div className="form-container">
        <form className="selections" onSubmit={this.handleSubmit}>
          <div className="input-array">
            <label>Conjunto de n√∫meros:</label><input type="text" placeholder="Ex...: 1, 2, 3, 4, 5"
             value={this.state.value} onChange={this.handleChange}/>
          </div>
          <input type="radio" name="BubbleSort" value={this.state.value} onClick={this.handleClick}/>BubbleSort
          <br />
          <input type="radio" name="InsertionSort" value="InsertionSort"/>InsertionSort
          {/* <br /> */}
          {/* <input type="radio" name="CoutingSort" value="CoutingSort"/>CoutingSort */}
          <br />
          <input type="radio" name="SelectionSort" value="SelectionSort"/>SelectionSort
          <input type="submit" value="Enviar"/>
        </form>
      </div>
    )
  }
}
